export type NamespaceMapping = {
    [name: string]: string;
};
export type NpmModuleMapping = {
    [name: string]: string;
};
export type BundleType = 'internal' | 'platform';
export interface BundleConfig {
    type: BundleType;
    name: string;
    namespace: string;
    namespaceMapping: NamespaceMapping;
    npmModuleMapping: NpmModuleMapping;
    files: {
        fileName: string;
        source: string;
    }[];
    enableKomaci?: boolean;
    strictCSSTokenCollection?: boolean;
    enableLuvio?: boolean;
    experimentalComplexExpressions?: boolean;
    apiVersion?: number;
    enableDeclarationsCollection?: boolean;
    enableClassReferences?: boolean;
    enableCustomDeclarationProperty?: boolean;
}
